operation.add:
function getDeliveryPoints() {    
    let zone = $('#collapse-shipping-address select[name=\'zone_id\']').val();    
    let shipping_method = $('input[name=shipping_method]:checked').val()

    if (shipping_method !== undefined && zone !== null) {
        if (shipping_method.indexOf('kazpost') > -1 || shipping_method.indexOf('exline') > -1) {			
			$('input[name *= city]').autocomplete({
                'source': function (request, response) {
                    $.ajax({
                        data: {
                            'shipping_method': shipping_method,
                            'country_id': $('#collapse-shipping-address select[name=\'country_id\']').val(),
                            'zone_id': zone,
                            'filter_name': request
                        },
                        url: 'index.php?route=checkout/kazshipping/autocomplete',
				        dataType: 'json',
				        //	global: false,
				        success: function (json) {
				            response($.map(json, function (item) {
				                return {
				                    label: item['title'],
				                    value: item['id']
				                }
				            }));
				        },
				        error: function (xhr, ajaxOptions, thrownError) {
                            console.log('Ошибка при определении пункта доставки Exline.');
                        }
			        });
			    },
                'select': function (item) {
                    $('input[name *= city]').val(item['label']);
                },
                'focus': function (event, ui) {
                    return false;
                },
		    });
	    }
	}
}

$(document).delegate('#collapse-shipping-address select[name=\'zone_id\']', 'change', getDeliveryPoints)
$(document).delegate('#collapse-shipping-address input[name=\'city\']', 'click', getDeliveryPoints)
